export function iconSelector(category: string) {
  switch (category) {
    case "Social":
      return <Social />;
    case "Finance":
      return <Finance />;
    case "Politics":
      return <Politics />;
    case "More":
      return <More />;
    case "Family":
      return <Family />;
    case "Movies":
      return <Movies />;
    case "Movies":
      return <Movies />;
    case "Videography":
      return <Videography />;
    case "Photography":
      return <Photography />;
    case "Games":
      return "🎮";
    case "Entertainment":
      return "🎭";
    case "Music":
      return "🎶";
    case "Sports":
      return "🏀";
    case "Business & Tech":
      return "🧠";
    case "Beauty & Fashion":
      return "💄";
    case "News & Politics":
      return "📰";
    case "Society & Culture":
      return "🏢";
    case "Wellness":
      return "🧘🏾‍♀️";
    case "Other":
      return "🙋🏾‍♂️";
    default:
      break;
  }
}

